select "交易编号","标题","链接","发布人","发布时间","简介" union all select docid,title,href,publisher,publish_time,description from crawl_info where title like '%建行%' or description like '%建行%' or title like '%建设银行%' or description like '%建设银行%' or title like '%银行%' or description like '%银行%' or title like '%信用卡%' or description like '%信用卡%' or title like '%贷款%' or description like '%贷款%' or title like '%金融%' or description like '%金融%' INTO outfile '/var/lib/mysql-files/output.csv' FIELDS TERMINATED BY ',' ENCLOSED BY '"' LINES TERMINATED BY '\n';